@@ -421,6 +422,10 @@ pp_cxx_primary_expression (cxx_pretty_printer *pp, tree t)
       pp_cxx_va_arg_expression (pp, t);
       break;

+    case OFFSETOF_EXPR:
+      pp_cxx_offsetof_expression (pp, t);
