 {
   KEEP (snprintf (0, 0, "%lc", L'\xff'));
   KEEP (snprintf (0, 0, "%lc", L'\xffff'));
-  KEEP (snprintf (0, 0, "%lc", wc));
-  KEEP (snprintf (0, 0, "%lc", ws3[2]));
+  KEEP (snprintf (0, 0, "%lc", (wint_t)wc));
