@@ -4021,15 +4021,15 @@ spu_function_arg (CUMULATIVE_ARGS *cum,
 {
   int byte_size;

-  if (cum >= MAX_REGISTER_ARGS)
+  if (*cum >= MAX_REGISTER_ARGS)
     return 0;

   byte_size = ((mode == BLKmode)
 	       ? int_size_in_bytes (type) : GET_MODE_SIZE (mode));

   /* The ABI does not allow parameters to be passed partially in
