+      && ! SYMBOL_REF_LOCAL_P (XEXP (fnaddr, 0)))
     use_reg (&use, pic_offset_table_rtx);

   if (TARGET_64BIT && INTVAL (callarg2) >= 0)
@@ -14854,7 +14854,7 @@ ix86_rtx_costs (x, code, outer_code, total)
