    type int_array is array (Natural range <>) of int;

    type pthread_mutex_t is record
-      m_short  : short_array (0 .. 1);
-      m_int    : int;
-      m_int1   : int_array (0 .. 3);
-      m_pad    : int;  --  needed for 32 bit ABI, but *not* for 64 bit
