@@ -2370,6 +2370,8 @@ gather_scalar_reductions (loop_p loop, reduction_info_table_type *reduction_list
   loop_vec_info simple_loop_info;

   simple_loop_info = vect_analyze_loop_form (loop);
+  if (simple_loop_info == NULL)
