       if (virtual_operand_p (res))
 	continue;

-      if (!simple_iv (loop, loop, res, &iv, true)
-	&& simple_loop_info)
-	{
-	   gimple reduc_stmt
-	     = vect_force_simple_reduction (simple_loop_info, phi, true,
-					    &double_reduc, true);
