 	      {
 		life[i] = true;
@@ -1956,7 +1957,7 @@ vect_bb_slp_scalar_cost (slp_tree node, vec<bool, va_stack> life)
     }

   FOR_EACH_VEC_ELT (SLP_TREE_CHILDREN (node), i, child)
