@@ -0,0 +1,50 @@
+// PR target/35364
+// { dg-do compile }
+// { dg-options "-O2 -fopenmp" }
+
+template <typename T>
+struct E
+{
+  E ();
+  ~E ();
+};
+
+template <typename T, typename U>
+struct C
+{
+  C (const U &y) : u (y) {}
+  ~C () {}
+  const U &u;
+};
+
+template <typename T, typename U = E<T> >
+struct B : public C<T, U>
+{
+  B (int x, const T &z = T (), const U &y = U ()) : C<T, U> (y) {}
+  ~B () {}
+};
+
+void
+foo ()
+{
+#pragma omp parallel
+  {
+    B<double> x (1);
+  }
+#pragma omp for
+  for (int i = 0; i < 10; i++)
+    {
+      B<int> x (i);
+    }
+#pragma omp sections
+  {
+#pragma omp section
+    {
+      B<int> x (6);
+    }
+  }
+#pragma omp single
+  {
+    B<int> x (16);
+  }
+}

@@ -1917,6 +1917,33 @@ remove_useless_stmts_1 (tree *tp, struct rus_data *data)
       data->last_goto = NULL;
       break;

+    case OMP_PARALLEL:
+      /* Make sure the outermost BIND_EXPR in OMP_BODY isn't removed
+	 as useless.  */
+      remove_useless_stmts_1 (&BIND_EXPR_BODY (OMP_BODY (*tp)), data);
+      data->last_goto = NULL;
+      break;
+
+    case OMP_SECTIONS:
+    case OMP_SINGLE:
+    case OMP_SECTION:
+    case OMP_MASTER :
+    case OMP_ORDERED:
+    case OMP_CRITICAL:
+      remove_useless_stmts_1 (&OMP_BODY (*tp), data);
+      data->last_goto = NULL;
+      break;
+
+    case OMP_FOR:
+      remove_useless_stmts_1 (&OMP_FOR_BODY (*tp), data);
+      data->last_goto = NULL;
+      if (OMP_FOR_PRE_BODY (*tp))
+	{
+	  remove_useless_stmts_1 (&OMP_FOR_PRE_BODY (*tp), data);
+	  data->last_goto = NULL;
+	}
+      break;
+
     default:
       data->last_goto = NULL;
       break;
