@@ -307,6 +302,18 @@ ao_ref_from_mem (ao_ref *ref, const_rtx mem)

   ref->ref_alias_set = MEM_ALIAS_SET (mem);

+  /* If MEM_OFFSET or MEM_SIZE are NULL we have to punt.
+     Keep points-to related information though.  */
+  if (!MEM_OFFSET (mem)
+      || !MEM_SIZE (mem))
+    {
+      ref->ref = NULL_TREE;
+      ref->offset = 0;
+      ref->size = -1;
+      ref->max_size = -1;
+      return true;
+    }
