+#include <bitset>
+#include <testsuite_hooks.h>
+
+// libstdc++/15361
+void test01()
+{
+  using namespace std;
+  bool test __attribute__((unused)) = true;
+
+  bitset<256> b;
+  b.set(225);
+  b.set(226);
+
+  VERIFY( b._Find_first() == 225 );
+  VERIFY( b._Find_next(225) == 226 );
+}
+
+int main()
+{
+  test01();
+  return 0;
+}
