@@ -55,4 +55,4 @@ chmod +x host_gnatmake
 # Limit the stack to 16MB for stack checking
 ulimit -s 16384

-exec $testdir/run_all.sh ${1+"$@"}
+exec $SHELL $testdir/run_all.sh ${1+"$@"}

