-	sp[1] = 0;
-      f__lx = atof (s);
-#ifdef Allow_TYQUAD
-      if (reqint & 2 && (se = sp - sp1 + exp) > 14 && se < 20)
-	{
-	  /* Assuming 64-bit longint and 32-bit long. */
-	  if (exp < 0)
