-	      f__llx = *sp1 - '0';
-	      while (++sp1 <= sp)
-		f__llx = 10 * f__llx + (*sp1 - '0');
-	    }
-	  while (--exp >= 0)
-	    f__llx *= 10;
-	  if (*s == '-')
-	    f__llx = -f__llx;
-	}
-#endif
-    }
-  else
-    f__lx = 0.;
-  if (havenum)
-    f__ltype = TYLONG;
-  else
-    switch (ch)
-      {
-      case ',':
-      case '/':
-	break;
-      default:
-	if (havestar && (ch == ' ' || ch == '\t' || ch == '\n'))
-	  break;
-	if (nml_read > 1)
-	  {
-	    f__lquit = 2;
-	    return 0;
-	  }
-	errfl (f__elist->cierr, 112, "invalid number");
-      }
-  return 0;
-}
