@@ -0,0 +1,17 @@
+/* { dg-options "-O1 -ffast-math -floop-interchange" } */
+
+int adler32(int adler, char *buf, int n)
+{
+  int sum = 0;
+  do {
+     adler += buf[0];
+     sum += adler;
+     adler += buf[1];
+     sum += adler;
+     adler += buf[2];
+     sum += adler;
+     adler += buf[3];
+     sum += adler;
+  } while (--n);
+  return adler | (sum << 16);
+}

@@ -0,0 +1,11 @@
+/* { dg-options "-O1 -funsafe-math-optimizations -floop-interchange" } */
+
+double f(double x)
+{
+  double y = 0.0;
+  int i;
+  for (i = 0; i < 8; i++) {
+    y += x * i;
+  }
+  return y;
+}
