 /* Allocate an rtx vector of N elements.
    Store the length, and initialize all elements to zero.  */
@@ -152,6 +160,12 @@ rtvec_alloc (int n)
   memset (&rt->elem[0], 0, n * sizeof (rtx));

   PUT_NUM_ELEM (rt, n);
+
+#ifdef GATHER_STATISTICS
+  rtvec_alloc_counts++;
