-		no_install= true; };
 host_modules= { module= libelf; lib_path=.libs; bootstrap=true;
 		extra_configure_flags='--disable-shared';
 		no_install= true; };
@@ -319,7 +314,6 @@ dependencies = { module=all-gcc; on=all-libiberty; hard=true; };
