+//980323 bkoz
+//test for bools with inclusive ors
+
+#include <assert.h>
+void bar ( bool  x ) {};
+void bars ( short  x ) {};
+
+int orb(){
+  bool y;
+  bar ( y );
+  int blob = ( 27 | int (y) );
+  return blob; //expect 27 or 0
+}
+
+int orbtrue(){
+  bool y = true;
+  bar ( y );
+  int blob = ( 27 | int (y) );
+  return blob; //expect 27
+}
+
+int orbfalse(){
+  bool y = false;
+  bar ( y );
+  int blob = ( 27 | int (y) );
+  return blob; //expect 27
+}
+
+int orbfalse2(){
+  bool y = 0;
+  bar ( y );
+  int blob = ( 27 | int (y) );
+  return blob;  //expect 27
+}
+
+int ors(){
+  short y = 1;
+  bars ( y );
+  int blob = ( 27 | int (y) );
+  return blob;  //expect 27
+}
+
+
+int orus(){
+  unsigned short y = 1;
+  bars ( y );
+  int blob = ( 65539 | int (y) );
+  return blob;  //expect 65539, will be 3 if done in us type
+}
+
+
+int main() {
+  int tmp;
+  tmp = orb();
+  assert (tmp == 27 || tmp == 0);
+  tmp = orbtrue();
+  assert (tmp ==27);
+  tmp = orbfalse();
+  assert (tmp ==27);
+  tmp = orbfalse2();
+  assert (tmp ==27);
+  tmp = ors();
+  assert (tmp ==27);
+  tmp = orus();
+  assert (tmp == 65539);
+
+  return 0;
+}
+
