-int
-scan_string (FILE *fp, sstring *s, int init)
-{
-  int c;
-
-  for (;;)
-    {
-      c = getc (fp);
-      if (c == EOF || c == '\n')
-	break;
-      if (c == init)
-	{
-	  c = getc (fp);
-	  break;
-	}
-      if (c == '\\')
-	{
-	  c = getc (fp);
-	  if (c == EOF)
-	    break;
-	  if (c == '\n')
-	    continue;
-	}
-      SSTRING_PUT (s, c);
-    }
-  MAKE_SSTRING_SPACE (s, 1);
-  *s->ptr = 0;
-  return c;
-}
-
-/* Skip horizontal white spaces (spaces, tabs, and C-style comments).  */
-
-int
-skip_spaces (FILE *fp, int c)
-{
-  for (;;)
-    {
-      if (c == ' ' || c == '\t')
-	c = getc (fp);
-      else if (c == '/')
-	{
-	  c = getc (fp);
-	  if (c != '*')
-	    {
-	      ungetc (c, fp);
-	      return '/';
-	    }
-	  c = getc (fp);
-	  for (;;)
-	    {
-	      if (c == EOF)
-		return EOF;
-	      else if (c != '*')
-		{
-		  if (c == '\n')
-		    source_lineno++, lineno++;
-		  c = getc (fp);
-		}
-	      else if ((c = getc (fp)) == '/')
-		return getc (fp);
-	    }
-	}
-      else
-	break;
-    }
-  return c;
-}
-
-int
-read_upto (FILE *fp, sstring *str, int delim)
-{
-  int ch;
-
-  for (;;)
-    {
-      ch = getc (fp);
-      if (ch == EOF || ch == delim)
-	break;
-      SSTRING_PUT (str, ch);
-    }
-  MAKE_SSTRING_SPACE (str, 1);
-  *str->ptr = 0;
-  return ch;
-}
-
-int
-get_token (FILE *fp, sstring *s)
-{
-  int c;
-
-  s->ptr = s->base;
- retry:
-  c = ' ';
-  c = skip_spaces (fp, c);
-  if (c == '\n')
-    {
-      source_lineno++;
-      lineno++;
-      goto retry;
-    }
-  if (c == '#')
-    {
-      c = get_token (fp, s);
-      if (c == INT_TOKEN)
-	{
-	  source_lineno = atoi (s->base) - 1; /* '\n' will add 1 */
-	  get_token (fp, &source_filename);
-	}
-      for (;;)
-	{
-	  c = getc (fp);
-	  if (c == EOF)
-	    return EOF;
-	  if (c == '\n')
-	    {
-	    source_lineno++;
-	    lineno++;
-	    goto retry;
-	    }
-	}
-    }
-  if (c == EOF)
-    return EOF;
-  if (ISDIGIT (c))
-    {
-      do
-	{
-	  SSTRING_PUT (s, c);
-	  c = getc (fp);
-	} while (c != EOF && ISDIGIT (c));
-      ungetc (c, fp);
-      c = INT_TOKEN;
-      goto done;
-    }
-  if (ISIDST (c))
-    {
-      c = scan_ident (fp, s, c);
-      ungetc (c, fp);
-      return IDENTIFIER_TOKEN;
-    }
-  if (c == '\'' || c == '"')
-    {
-      c = scan_string (fp, s, c);
-      ungetc (c, fp);
-      return c == '\'' ? CHAR_TOKEN : STRING_TOKEN;
-    }
-  SSTRING_PUT (s, c);
- done:
-  MAKE_SSTRING_SPACE (s, 1);
-  *s->ptr = 0;
-  return c;
-}
-
-unsigned int
-hashstr (const char *str, unsigned int len)
-{
-  unsigned int n = len;
-  unsigned int r = 0;
-  const unsigned char *s = (const unsigned char *) str;
-
-  do
-    r = r * 67 + (*s++ - 113);
-  while (--n);
-  return r + len;
-}

