@@ -238,11 +238,10 @@ package body Back_End is
       --  Loop through command line arguments, storing them for later access

       while Next_Arg < save_argc loop
-
          Look_At_Arg : declare
-            Argv_Ptr  : constant BSP    := save_argv (Next_Arg);
