   super_type = gfc_get_derived_super_type (sym);

@@ -11169,25 +11168,10 @@ resolve_fl_derived (gfc_symbol *sym)
 	    && sym != c->ts.u.derived)
 	add_dt_to_dt_list (c->ts.u.derived);

-      if (c->attr.pointer || c->attr.proc_pointer || c->attr.allocatable
