+// 20.7.1 Template class unique_ptr [unique.ptr]
+
+#include <memory>
+#include <testsuite_hooks.h>
+
+struct A;
+
+struct B
+{
+  std::unique_ptr<A> a;
+};
+
+struct A
+{
+  B* b;
+  ~A() { VERIFY(b->a != nullptr); }
+};
+
+void test01()
+{
+  B b;
+  b.a.reset(new A);
+  b.a->b = &b;
+}
+
+struct C;
+
+struct D
+{
+  std::unique_ptr<C[]> c;
+};
+
+struct C
+{
+  D* d;
+  ~C() { VERIFY(d->c != nullptr); }
+};
+
+void test02()
+{
+  D d;
+  d.c.reset(new C[1]);
+  d.c[0].d = &d;
+}
+
+int main()
+{
+  test01();
+  test02();
+}

