+	if len(a) == len(b) {
+		atomic.AddInt32(&cnt32, 2)
+	}
+	atomic.AddInt32(&cnt32, 4)
+	if len(a) >= len(b) {
+		atomic.AddInt32(&cnt32, 8)
+	}
+	if len(a) <= len(b) {
+		atomic.AddInt32(&cnt32, 16)
+	}
+	return atomic.LoadInt32(&cnt32) == 31
+}
