+
+      for (insn = f; insn; insn = NEXT_INSN (insn))
+	{
+	  if (GET_CODE (insn) == INSN
+	      && GET_CODE (PATTERN (insn)) == CLOBBER)
+	    {
+              PUT_CODE (insn, NOTE);
+              NOTE_LINE_NUMBER (insn) = NOTE_INSN_DELETED;
+              NOTE_SOURCE_FILE (insn) = 0;
+	    }
+	}
+    }
   bzero (regs_ever_live, sizeof regs_ever_live);

