@@ -1494,15 +1494,15 @@ print_operand (file, x, code)

     case CONST_INT:
       if (code == 'b')
-        fprintf (file, "%d", INTVAL (x) & 0xff);
+        fprintf (file, "%d", (int)(INTVAL (x) & 0xff));
       else if (code == 'X')
-        fprintf (file, "%d", INTVAL (x) & 0xff);
+        fprintf (file, "%d", (int)(INTVAL (x) & 0xff));
       else if (code == 'x')
-        fprintf (file, "0x%x", INTVAL (x) & 0xffff);
+        fprintf (file, "0x%x", (int)(INTVAL (x) & 0xffff));
