-        fprintf (file, "%d", (INTVAL (x) << 16) >> 16);
+        fprintf (file, "%d", (int)(INTVAL (x) << 16) >> 16);
       else
-        fprintf (file, "%d", INTVAL (x));
+        fprintf (file, "%d", (int)INTVAL (x));
       break;

     default:
