+
+template <typename T>
+struct S {
+    [[deprecated("foo")]] unsigned m_fn (char const chr)
+    {
+        using index_t = unsigned;
+        return T::arr[static_cast<index_t>(chr)]; // { dg-bogus "deprecated" }
+    }
+};
+
+extern unsigned int arr[];
+
+struct R {
+    [[deprecated("foo")]] unsigned m_fn (char const chr)
+    {
