   int num_args = 1;

   argc = *in_argc;
   argv = *in_argv;
-
+  added_libraries = *in_added_libraries;

   args = (int *) xmalloc (argc * sizeof (int));
   bzero ((char *) args, argc * sizeof (int));
@@ -231,16 +235,23 @@ lang_specific_driver (fn, in_argc, in_argv)

   /* Add `-lstdc++' if we haven't already done so.  */
   if (library)
-    arglist[j++] = "-lstdc++";
+    {
+      arglist[j++] = "-lstdc++";
+      added_libraries++;
+    }
   if (saw_math)
     arglist[j++] = saw_math;
   else if (library)
-    arglist[j++] = MATH_LIBRARY;
+    {
+      arglist[j++] = MATH_LIBRARY;
+      added_libraries++;
+    }
   if (saw_libc)
     arglist[j++] = saw_libc;

   arglist[j] = NULL;

   *in_argc = j;
   *in_argv = arglist;
+  *in_added_libraries = added_libraries;
 }
