+
+   subtype Tag20_Nonstatic      is Tag (Len => Sub_Length'Last);
+   subtype Tag20_Dupl_Nonstatic is Tag (Sub_Length'Last);
+   subtype Tag20_Same_Nonstatic is Tag20_Nonstatic;
