@@ -3902,14 +3902,14 @@
   "
 {
   rtx pat;
-  operands[0] = gen_lowpart (SImode, operands[0]);
   /* In -fPIC mode the constructs like (const (unspec [symbol_ref]))
      may confuse gen_lowpart.  */
