@@ -1370,7 +1370,39 @@
   ""
   "*
 {
-  return \"ret [d2,d3,a2,a3],%0\";
+  int i, need_comma;
+  int d2, d3, a2, a3;
+
+  need_comma = 0;
+  fputs (\"\\tret [\", asm_out_file);
+  if (regs_ever_live[2])
+    {
+      fputs (\"d2\", asm_out_file);
+      need_comma = 1;
+    }
+  if (regs_ever_live[3])
+    {
+      if (need_comma)
+	fputc (',', asm_out_file); 
+      fputs (\"d3\", asm_out_file);
+      need_comma = 1;
+    }
+  if (regs_ever_live[6])
+    {
+      if (need_comma)
+	fputc (',', asm_out_file); 
+      fputs (\"a2\", asm_out_file);
+      need_comma = 1;
+    }
+  if (regs_ever_live[7])
+    {
+      if (need_comma)
+	fputc (',', asm_out_file); 
+      fputs (\"a3\", asm_out_file);
+      need_comma = 1;
+    }
+  fprintf (asm_out_file, \"],%d\\n\", INTVAL (operands[0]));
+  return \"\";
 }"
   [(set_attr "cc" "clobber")])

