 	  int default_len = sizeof GCC_INCLUDE_DIR - 8;
 	  int specd_len = strlen (specd_prefix);

-	  default_len = sizeof GCC_INCLUDE_DIR - 8;
 	  memcpy (default_prefix, GCC_INCLUDE_DIR, default_len);
 	  default_prefix[default_len] = '\0';
