+#ifdef STRICT_ANSI
+fix = {
+    hackname = strict_ansi;
+    select   = "__STDC__[ \t]*[=!]=[ \t]*[01]";
+    sed      = "s/__STDC__[ \t]*==[ \t]*0/!defined (__STRICT_ANSI__)/g";
+    sed      = "s/__STDC__[ \t]*!=[ \t]*0/defined (__STRICT_ANSI__)/g";
+    sed      = "s/__STDC__[ \t]*==[ \t]*1/defined (__STRICT_ANSI__)/g";
