+		      && REGNO_REG_SET_P (live_at_end, i)
+		      && !FUNCTION_VALUE_REGNO_P (i)))
 	      {
 		/* We do not want REG_UNUSED notes for these registers.  */
 		mark_set_1 (pbi, CLOBBER, regno_reg_rtx[i], cond, insn,
