 	{
-	  static_assert(__is_callable<_Deleter(_Yp*)>::value,
+	  static_assert(__is_callable<_Deleter&(_Yp*&)>::value,
 	      "deleter expression d(p) is well-formed");
 	  _M_enable_shared_from_this_with(__p);
