@@ -322,12 +323,13 @@ skip_white_space (c)
 	  c = check_newline ();
 	  break;

+	case '\r':
 	  /* Per C99, horizontal whitespace is just these four characters.  */
 	case ' ':
 	case '\t':
 	case '\f':
 	case '\v':
-	    c = getch ();
+	  c = getch ();
 	  break;

 	case '\\':
@@ -1503,9 +1505,10 @@ c_lex (value)
       case '\t':
       case '\f':
       case '\v':
-	  c = getch ();
+	c = getch ();
 	break;

+      case '\r':
       case '\n':
