+    host_xmake_file=alpha/x-vms
+    host_exeext=.exe
+    # This removes the cpu type and manufacturer components and
+    #  replaces "." with "_" in the operating system version.
+    target_alias=`echo $host | sed 's/.*-.*-\(.*\)$/\1/' | sed 's/\./_/g'`
+    prefix=/gnu
+    local_prefix=/gnu
