@@ -351,7 +351,7 @@ layout_decl (decl, known_align)
       register enum machine_mode xmode
 	= mode_for_size_tree (DECL_SIZE (decl), MODE_INT, 1);

-      if (xmode != BLKmode && known_align > GET_MODE_ALIGNMENT (xmode))
+      if (xmode != BLKmode && known_align >= GET_MODE_ALIGNMENT (xmode))
