+                                    &type_specifiers);
+
       /* At this point this is surely not elaborated type specifier.  */
       if (!cp_parser_parse_definitely (parser))
 	return NULL_TREE;

       if (cxx_dialect == cxx98)
         maybe_warn_cpp0x ("scoped enums");

-      /* Consume the `:'.  */
