@@ -0,0 +1,18 @@
+// { dg-do compile }
+
+// PR c++/3663
+// Enforce access of nested type.
+
+template <typename A>
+class S {
+  class T {};				// { dg-error "private" }
+};
+
+template <typename A>
+typename A::T* f (A) {			// { dg-error "this context" }
+  return 0;
+}
+
+void g () {
+  f (S<int> ());			// { dg-error "context|instantiated" }
+}
