       if (data != NULL && bitmap_clear_bit ((bitmap) data, DECL_UID (t))
 	  && DECL_CONTEXT (t) == current_function_decl)
 	mark_all_vars_used (&DECL_INITIAL (t), data);
-      set_is_used (t);
+      if (var_ann (t) != NULL)
+	set_is_used (t);
     }
