-/* Implement ASM_OUTPUT_OPCODE.  */
-const char *
-mt_asm_output_opcode (FILE *f ATTRIBUTE_UNUSED, const char *ptr)
-{
-  if (mt_nops_required)
-    fprintf (f, ";# need %d nops because of %s\n\t",
-	     mt_nops_required, mt_nop_reasons);
-  
-  while (mt_nops_required)
-    {
-      fprintf (f, "nop\n\t");
-      -- mt_nops_required;
-    }
-  
-  return ptr;
-}
