-/* Generate code for the "eh_epilogue" pattern.  */
-void
-mt_emit_eh_epilogue (rtx * operands ATTRIBUTE_UNUSED)
-{
-  cfun->machine->eh_stack_adjust = EH_RETURN_STACKADJ_RTX; /* operands[0]; */
-  mt_expand_epilogue (EH_EPILOGUE);
-}
