 /* Allow dumping of an expression in the debugger.  */
@@ -62,6 +63,18 @@ gfc_debug_expr (gfc_expr *e)
   dumpfile = tmp;
 }

+/* Allow for dumping of a piece of code in the debugger.  */
+void gfc_debug_code (gfc_code *c);
+
+void
+gfc_debug_code (gfc_code *c)
+{
+  FILE *tmp = dumpfile;
+  dumpfile = stderr;
+  show_code (1, c);
+  fputc ('\n', dumpfile);
+  dumpfile = tmp;
+}

