@@ -402,15 +461,15 @@ fix = {
     files    = "sys/stat.h";

     sed = "/^static int[ \t]*[a-z]*stat(/i\\\n"
-	"#ifdef __cplusplus\\\n"
-	"extern \"C\"\\\n"
-	"{\\\n"
-	"#endif\\\n";
+        "#ifdef __cplusplus\\\n"
+        "extern \"C\"\\\n"
+        "{\\\n"
+        "#endif\\\n";

     sed = "/^}$/a\\\n"
-	"#ifdef __cplusplus\\\n"
-	"}\\\n"
-	"#endif \/* __cplusplus *\/\\\n";
+        "#ifdef __cplusplus\\\n"
+        "}\\\n"
+        "#endif \/* __cplusplus *\/\\\n";
 };


@@ -1235,15 +1294,14 @@ fix = {
 /*
  *  Fix return type of fread and fwrite on sysV68
  */
-#ifdef LATER
 fix = {
     hackname = read_ret_type;
     files    = stdio.h;
+    select   = "extern int\t.*, fread\\(\\), fwrite\\(\\)";
     sed      = "s/^\\(extern int\tfclose(), fflush()\\), "
                  "\\(fread(), fwrite()\\)\\(.*\\)$"
                "/extern unsigned int\t\\2;\\\n\\1\\3/";
 };
-#endif


