    pragma Convention (C, timeval);

@@ -90,7 +90,6 @@ package body System.OS_Interface is
       S : time_t;
       F : Duration;

-      use type System.Linux.time_t;
    begin
       S := time_t (Long_Long_Integer (D));
       F := D - Duration (S);
