+static void
+s390_def_or_undef_macro (cpp_reader *pfile,
+			 unsigned int mask,
+			 const struct cl_target_option *old_opts,
+			 const struct cl_target_option *new_opts,
+			 const char *macro_def, const char *macro_undef)
