+
+int a[10];
+
+void
+foo (void)
+{
+  int x;
+  int i;
+  for (i = 0; i < 1;)
+    {
+      int b[3];
+      for (i = 0; i < 1; i++)
+	b[i] = a[i];
+      if (&x)
+	a[0] = b[0];
+    }
+}

