+	      PUT_MODE (copy, mode);
+	      XEXP (copy, 0)
+		= map->label_map[CODE_LABEL_NUMBER (XEXP (constant, 0))];
+	      LABEL_OUTSIDE_LOOP_P (copy) = LABEL_OUTSIDE_LOOP_P (orig);
+	      copy = force_const_mem (Pmode, copy);
+	      return XEXP (copy, 0);
+	    }
