@@ -40,12 +40,19 @@ class _Jv_name_finder
   ~_Jv_name_finder ()
     {
 #if defined (HAVE_PIPE) && defined (HAVE_FORK)
-      close (f_pipe[1]);
-      fclose (b_pipe_fd);
-
-      int wstat;
-      // We don't care about errors here.
-      waitpid (pid, &wstat, 0);
+      myclose (f_pipe[0]);
+      myclose (f_pipe[1]);
+      myclose (b_pipe[0]);
+      myclose (b_pipe[1]);
+      if (b_pipe_fd != NULL)
+	fclose (b_pipe_fd);
+
+      if (pid >= 0)
+	{
+	  int wstat;
+	  // We don't care about errors here.
+	  waitpid (pid, &wstat, 0);
+	}
 #endif
     }

@@ -72,5 +79,13 @@ class _Jv_name_finder
   int f_pipe[2], b_pipe[2];
   FILE *b_pipe_fd;
   int error;
+
+  // Close a descriptor only if it has not been closed.
+  void myclose (int fd)
+  {
+    if (fd != -1)
+      close (fd);
+  }
+
 #endif
 };

